import React, { useContext, useEffect, useState, useCallback } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Dropdown from "react-bootstrap/Dropdown";
import Tables from "../../components/tables/Tables";
import Spiner from "../../components/spinner/Spiner";
import { useNavigate } from "react-router-dom";
import {
  addData,
  dltdata,
  updateData,
} from "../../components/context/ContextProvider";
import { usergetfunc, deletefunc } from "../../services/Apis";
import Alert from "react-bootstrap/Alert";
import { toast } from "react-toastify";

import "./home.css";

const Home = () => {
  // for the "get all users"
  const [userdata, setUserData] = useState([]);
  const [showspin, setShowSpin] = useState(true);
  const [search, setSearch] = useState("");
  const [gender, setGender] = useState("All");
  const [leave_type, setLeaveType] = useState("All");
  const [sort, setSort] = useState("Newest");

  const { useradd, setUseradd } = useContext(addData);
  const { update, setUpdate } = useContext(updateData);
  const { deletedata, setDLtdata } = useContext(dltdata);

  const navigate = useNavigate();

  const adduser = () => {
    navigate("/select-staff-type");
  };

  // for the "get all users"
  const userGet = useCallback(async () => {
    const response = await usergetfunc(search, gender, leave_type, sort); // Pass search query to the API

    if (response.status === 200) {
      setUserData(response.data);
    } else {
      console.log("Error getting user data");
    }
  }, [search, gender, leave_type, sort]);

  // user delete
  const deleteUser = async (id) => {
    const response = await deletefunc(id);
    if (response.status === 200) {
      userGet(); // Refresh user list after delete
      setDLtdata(response.data); // Save deleted user data
      toast.success(`User ${response.data.fname} deleted successfully`);
    } else {
      toast.error("Error deleting user");
    }
  };

  useEffect(() => {
    // to get all the users
    userGet();
    setTimeout(() => {
      setShowSpin(false);
    }, 1200);
  }, [userGet]); // Now includes `userGet` as a dependency

  return (
    <>
      {useradd ? (
        <Alert variant="success" onClose={() => setUseradd("")} dismissible>
          HOSPITAL STAFF MEMBER {useradd.fname.toUpperCase()}, SUCCESSFULLY
          ADDED
        </Alert>
      ) : (
        ""
      )}
      {update ? (
        <Alert variant="primary" onClose={() => setUpdate("")} dismissible>
          HOSPITAL STAFF MEMBER {update.fname.toUpperCase()}, SUCCESSFULLY
          UPDATED
        </Alert>
      ) : (
        ""
      )}
      {deletedata && deletedata.fname ? (
        <Alert variant="danger" onClose={() => setDLtdata("")} dismissible>
          HOSPITAL STAFF MEMBER {deletedata.fname.toUpperCase()}, SUCCESSFULLY
          DELETED
        </Alert>
      ) : (
        ""
      )}
      <div className="container">
        <div className="main_div">
          {/* search add btn */}
          <div className="search_add mt-4 d-flex justify-content-between">
            <div className="search col-lg-4">
              <Form className="d-flex">
                <Form.Control
                  type="search"
                  placeholder="Search"
                  className="me-2"
                  aria-label="Search"
                  onChange={(e) => setSearch(e.target.value)} // Update search state
                />
                <Button variant="success" className="search_btn">
                  <i className="fa-solid fa-magnifying-glass"></i>
                </Button>
              </Form>
            </div>
            <div className="add_btn">
              <Button variant="primary" onClick={adduser}>
                <i className="fa-solid fa-plus"></i>&nbsp; Add Staff
              </Button>
            </div>
          </div>
          <div className="filter_div mt-5 d-flex justify-content-between flex-wrap">
            <div className="export_csv">
              <Button className="export_btn">Export to CSV</Button>
            </div>

            <div className="filter_gender">
              <div className="filter">
                <p>Filter by gender</p>
                <div className="gender d-flex justify-content-between">
                  <Form.Check
                    type={"radio"}
                    label={`All`}
                    name="gender"
                    value={"All"}
                    className="radio-spacing"
                    defaultChecked
                    onChange={(e) => setGender(e.target.value)}
                  />
                  <Form.Check
                    type={"radio"}
                    label={`Male`}
                    name="gender"
                    value={"Male"}
                    onChange={(e) => setGender(e.target.value)}
                    className="radio-spacing"
                  />
                  <Form.Check
                    type={"radio"}
                    label={`Female`}
                    name="gender"
                    value={"Female"}
                    onChange={(e) => setGender(e.target.value)}
                  />
                </div>
              </div>
            </div>

            <div className="filter_newold">
              <p>Filter by date</p>
              <Dropdown className="text-center">
                <Dropdown.Toggle className="dropdown_btn" id="dropdown-basic">
                  <i className="fa-solid fa-calendar-week"></i>
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  <Dropdown.Item onClick={() => setSort("Newest")}>
                    Newest
                  </Dropdown.Item>
                  <Dropdown.Item onClick={() => setSort("Oldest")}>
                    Oldest
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </div>

            <div className="filter_leave_type">
              <div className="leave_type">
                <p>Filter by leave type</p>
                <div className="leave_type_radio d-flex justify-content-between flex-wrap">
                  <Form.Check
                    type={"radio"}
                    label={`All`}
                    name="leave_type"
                    value={"All"}
                    onChange={(e) => setLeaveType(e.target.value)}
                    className="radio-spacing"
                    defaultChecked
                  />
                  <Form.Check
                    type={"radio"}
                    label={`Casual`}
                    name="leave_type"
                    value={"Casual"}
                    onChange={(e) => setLeaveType(e.target.value)}
                    className="radio-spacing"
                  />
                  <Form.Check
                    type={"radio"}
                    label={`Medical`}
                    name="leave_type"
                    value={"Medical"}
                    onChange={(e) => setLeaveType(e.target.value)}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
        {showspin ? (
          <Spiner />
        ) : (
          <Tables userdata={userdata} deleteUser={deleteUser} />
        )}
      </div>
    </>
  );
};

export default Home;
