import React, { useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Select from "react-select";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Row from "react-bootstrap/Row";

import "./edit.css";

const Edit = () => {
  const [inputdata, setInputData] = useState({
    fname: "",
    lname: "",
    email: "",
    mobile: "",
    gender: "",
    echelon: "",
  });

  const [status, setStatus] = useState("Active");
  const [image, setImage] = useState("");
  const [preview, setPreview] = useState("");

  // status options
  const options = [
    { value: "Active", label: "Active" },
    { value: "Inactive", label: "Inactive" },
  ];

  // setInput Value
  const setInputValue = (e) => {
    const { name, value } = e.target;
    setInputData({ ...inputdata, [name]: value });
  };

  // status set
  const setStatusValue = (e) => {
    setStatus(e.value);
  };

  // profile set
  const setProfile = (e) => {
    setImage(e.target.files[0]);
  };

  // submit user data
  const submitUserData = async (e) => {
    e.preventDefault();

    const { fname, lname, email, mobile, gender, echelon } = inputdata;

    if (fname === "") {
      toast.error("First name is required!");
    } else if (lname === "") {
      toast.error("Last name is required!");
    } else if (email === "") {
      toast.error("Email is required!");
    } else if (!email.includes("@")) {
      toast.error("Enter a valid email!");
    } else if (mobile === "") {
      toast.error("Mobile number is required!");
    } else if (mobile.length !== 10) {
      toast.error("Enter a valid mobile number!");
    } else if (gender === "") {
      toast.error("Gender is required!");
    } else if (status === "") {
      toast.error("Status is required!");
    } else if (image === "") {
      toast.error("Profile picture is required!");
    } else if (echelon === "") {
      toast.error("Echelon is required!");
    } else if (leaves === "") {
      toast.error("Number of leaves is required!");
    } else {
      toast.success("Details updated successfully!");
    }
  };

  useEffect(() => {
    if (image) {
      setPreview(URL.createObjectURL(image));
    }
  }, [image]);
  return (
    <>
      <div className="container">
        <h2 className="text-center mt-1">Update Details</h2>
        <Card className="shadow mt-3 p-3">
          <div className="profile_div text-center">
            <img
              src={preview ? preview : "/hospital-staff.jpg"}
              alt="hospital-staff"
            />
          </div>
          <Form autocomplete="off">
            <Row>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>First name</Form.Label>
                <Form.Control
                  type="text"
                  name="fname"
                  value={inputdata.fname}
                  onChange={setInputValue}
                  placeholder="Enter first name"
                />
              </Form.Group>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Last name</Form.Label>
                <Form.Control
                  type="text"
                  name="lname"
                  value={inputdata.lname}
                  onChange={setInputValue}
                  placeholder="Enter last name"
                />
              </Form.Group>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={inputdata.email}
                  onChange={setInputValue}
                  placeholder="Enter email address"
                />
              </Form.Group>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Mobile number</Form.Label>
                <Form.Control
                  type="text"
                  name="mobile"
                  value={inputdata.mobile}
                  onChange={setInputValue}
                  placeholder="Enter mobile number (without country code: +94)"
                />
              </Form.Group>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Select Gender (1 of 2)</Form.Label>
                <Form.Check // prettier-ignore
                  type={"radio"}
                  label={`Male`}
                  name="gender"
                  value={"Male"}
                  onChange={setInputValue}
                />
                <Form.Check // prettier-ignore
                  type={"radio"}
                  label={`Female`}
                  name="gender"
                  value={"Female"}
                  onChange={setInputValue}
                />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Select status</Form.Label>
                <Select
                  options={options}
                  value={status}
                  onChange={setStatusValue}
                />
              </Form.Group>

              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Add a profile picture</Form.Label>
                <Form.Control
                  type="file"
                  name="user_profile"
                  onChange={setProfile}
                  placeholder="Add a profile picture"
                />
              </Form.Group>
              <Form.Group className="mb-3 col-lg-6" controlId="formBasicEmail">
                <Form.Label>Echelon</Form.Label>
                <Form.Control
                  type="text"
                  name="echelon"
                  value={inputdata.echelon}
                  onChange={setInputValue}
                  placeholder="Enter the appropriate echelon"
                />
              </Form.Group>
            </Row>
            <Button variant="primary" type="submit" onClick={submitUserData}>
              Update
            </Button>
          </Form>
        </Card>
        <ToastContainer position="top-center" />
      </div>
    </>
  );
};

export default Edit;
