import React from "react";
import { Pagination } from "react-bootstrap";

const Paginations = ({ page, setPage, totalPages }) => {
  const handlePrev = () => {
    if (page > 1) {
      setPage(page - 1); // Go to the previous page
    }
  };

  const handleNext = () => {
    if (page < totalPages) {
      setPage(page + 1); // Go to the next page
    }
  };

  const handlePageClick = (pageNumber) => {
    setPage(pageNumber); // Set the page to the clicked page number
  };

  return (
    <Pagination className="mt-3">
      <Pagination.Prev onClick={handlePrev} disabled={page === 1} />

      {[...Array(totalPages).keys()].map((pageNumber) => (
        <Pagination.Item
          key={pageNumber + 1}
          active={page === pageNumber + 1}
          onClick={() => handlePageClick(pageNumber + 1)}
        >
          {pageNumber + 1}
        </Pagination.Item>
      ))}

      <Pagination.Next onClick={handleNext} disabled={page === totalPages} />
    </Pagination>
  );
};

export default Paginations;
