import React, { useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Select from "react-select";
import Spiner from "../../components/spinner/Spiner";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Row from "react-bootstrap/Row";

import "./register.css";

const MinorStaff = () => {
  const [inputdata, setInputData] = useState({
    fname: "",
    lname: "",
    email: "",
    mobile: "",
    gender: "",
    echelon: "",
    leaves: 0,
  });

  const [status, setStatus] = useState({ value: "Casual", label: "Casual" });
  const [image, setImage] = useState("");
  const [preview, setPreview] = useState("");

  const [showspin, setShowSpin] = useState(true);

  // status options
  const options = [
    { value: "Casual", label: "Casual" },
    { value: "Medical", label: "Medical" },
  ];

  // setInput Value
  const setInputValue = (e) => {
    const { name, value } = e.target;
    setInputData({
      ...inputdata,
      [name]: name === "leaves" ? parseInt(value) : value, // Parse leaves to an integer
    });
  };

  // status set
  const setStatusValue = (selectedOption) => {
    setStatus(selectedOption);
  };

  // profile set
  const setProfile = (e) => {
    setImage(e.target.files[0]);
  };

  // submit user data
  const submitUserData = async (e) => {
    e.preventDefault();

    const { fname, lname, email, mobile, gender, echelon, leaves } = inputdata;

    if (fname === "") {
      toast.error("First name is required!");
    } else if (lname === "") {
      toast.error("Last name is required!");
    } else if (email === "") {
      toast.error("Email is required!");
    } else if (!email.includes("@")) {
      toast.error("Enter a valid email!");
    } else if (mobile === "") {
      toast.error("Mobile number is required!");
    } else if (mobile.length !== 10) {
      toast.error("Enter a valid mobile number!");
    } else if (gender === "") {
      toast.error("Gender is required!");
    } else if (!status.value) {
      toast.error("Type of leave is required!");
    } else if (isNaN(leaves) || leaves <= 0) {
      // Ensure leaves is a positive number
      toast.error("Number of leaves is required and must be greater than 0!");
    } else if (leaves > 45) {
      // Ensure leaves do not exceed 45
      toast.error("Number of leaves cannot exceed 45!");
    } else if (image === "") {
      toast.error("Profile picture is required!");
    } else if (echelon === "") {
      toast.error("Echelon is required!");
    } else {
      toast.success("Staff member added successfully!");
      // You would handle the form submission here
    }
  };

  useEffect(() => {
    if (image) {
      setPreview(URL.createObjectURL(image));
    }
    setTimeout(() => {
      setShowSpin(false);
    }, 1200);
  }, [image]);

  return (
    <>
      {showspin ? (
        <Spiner />
      ) : (
        <div className="staff-add-form-bg">
          <div className="staff-add-container">
            <h2 className="text-center mt-1" id="title-text">
              Add Minor Staff
            </h2>
            <Card className="shadow mt-3 p-3">
              <div className="profile_div text-center">
                <img
                  src={preview ? preview : "/add-minor-staff.jpg"}
                  alt="hospital-staff"
                />
              </div>
              <Form autoComplete="off">
                <Row>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>First name</Form.Label>
                    <Form.Control
                      type="text"
                      name="fname"
                      value={inputdata.fname}
                      onChange={setInputValue}
                      placeholder="Enter first name"
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Last name</Form.Label>
                    <Form.Control
                      type="text"
                      name="lname"
                      value={inputdata.lname}
                      onChange={setInputValue}
                      placeholder="Enter last name"
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Email address</Form.Label>
                    <Form.Control
                      type="email"
                      name="email"
                      value={inputdata.email}
                      onChange={setInputValue}
                      placeholder="Enter email address"
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Mobile number</Form.Label>
                    <Form.Control
                      type="text"
                      name="mobile"
                      value={inputdata.mobile}
                      onChange={setInputValue}
                      placeholder="Enter mobile number (without country code: +94)"
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Select Gender (1 of 2)</Form.Label>
                    <Form.Check // prettier-ignore
                      type={"radio"}
                      label={`Male`}
                      name="gender"
                      value={"Male"}
                      onChange={setInputValue}
                    />
                    <Form.Check // prettier-ignore
                      type={"radio"}
                      label={`Female`}
                      name="gender"
                      value={"Female"}
                      onChange={setInputValue}
                    />
                  </Form.Group>

                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Select the type of leave</Form.Label>
                    <Select
                      options={options}
                      value={status} // This now takes the entire object
                      onChange={setStatusValue}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Number of leaves</Form.Label>
                    <Form.Control
                      type="number"
                      min="0"
                      max="45"
                      name="leaves"
                      value={inputdata.leaves}
                      onChange={setInputValue}
                      placeholder="Enter the number of leaves"
                    />
                  </Form.Group>

                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Add a profile picture</Form.Label>
                    <Form.Control
                      type="file"
                      name="user_profile"
                      onChange={setProfile}
                      placeholder="Add a profile picture"
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3 col-lg-6"
                    controlId="formBasicEmail"
                  >
                    <Form.Label>Echelon</Form.Label>
                    <Form.Control
                      type="text"
                      name="echelon"
                      value={inputdata.echelon}
                      onChange={setInputValue}
                      placeholder="Enter the appropriate echelon"
                    />
                  </Form.Group>
                </Row>
                <Button
                  variant="primary"
                  type="submit"
                  onClick={submitUserData}
                >
                  Add Minor Staff Member
                </Button>
              </Form>
            </Card>
            <ToastContainer position="top-center" />
          </div>
        </div>
      )}
    </>
  );
};

export default MinorStaff;
