import React, { useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Select from "react-select";
import Spiner from "../../components/spinner/Spiner";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Row from "react-bootstrap/Row";

// Import API call
import { registerMajorStaff } from "../../services/Apis";

import "./register.css";

const MajorStaff = () => {
  const [inputdata, setInputData] = useState({
    fname: "",
    lname: "",
    email: "",
    mobile: "",
    gender: "",
    echelon: "",
    leaves: 0,
  });

  const [leave_type, setStatus] = useState({
    value: "Casual",
    label: "Casual",
  });
  const [image, setImage] = useState("");
  const [preview, setPreview] = useState("");
  const [showspin, setShowSpin] = useState(true);

  const options = [
    { value: "Casual", label: "Casual" },
    { value: "Medical", label: "Medical" },
  ];

  const setInputValue = (e) => {
    const { name, value } = e.target;
    setInputData({
      ...inputdata,
      [name]: name === "leaves" ? parseInt(value) : value, // Parse leaves to an integer
    });
  };

  const setStatusValue = (selectedOption) => {
    setStatus(selectedOption);
  };

  const setProfile = (e) => {
    setImage(e.target.files[0]);
  };

  const submitUserData = async (e) => {
    e.preventDefault();

    const { fname, lname, email, mobile, gender, echelon, leaves } = inputdata;

    if (fname === "") {
      toast.error("First name is required!");
    } else if (lname === "") {
      toast.error("Last name is required!");
    } else if (email === "") {
      toast.error("Email is required!");
    } else if (!email.includes("@")) {
      toast.error("Enter a valid email!");
    } else if (mobile === "") {
      toast.error("Mobile number is required!");
    } else if (mobile.length !== 10) {
      toast.error("Enter a valid mobile number!");
    } else if (gender === "") {
      toast.error("Gender is required!");
    } else if (!leave_type.value) {
      toast.error("Type of leave is required!");
    } else if (isNaN(leaves) || leaves <= 0) {
      toast.error("Number of leaves is required and must be greater than 0!");
    } else if (leaves > 45) {
      toast.error("Number of leaves cannot exceed 45!");
    } else if (image === "") {
      toast.error("Profile picture is required!");
    } else if (echelon === "") {
      toast.error("Echelon is required!");
    } else {
      const data = new FormData();
      data.append("fname", fname);
      data.append("lname", lname);
      data.append("email", email);
      data.append("mobile", mobile);
      data.append("gender", gender);
      data.append("leave_type", leave_type.value); // Set leave_type value
      data.append("leaves", leaves);
      data.append("user_profile", image);
      data.append("echelon", echelon);

      const config = {
        "Content-Type": "multipart/form-data",
      };
      const response = await registerMajorStaff(data, config);

      if (response.status === 200) {
        setInputData({
          fname: "",
          lname: "",
          email: "",
          mobile: "",
          gender: "",
          echelon: "",
          leaves: 0,
        });
        setStatus("");
        setImage("");
        toast.success("Major Staff added successfully!");
      } else {
        toast.error("An error occurred.");
      }
    }
  };

  useEffect(() => {
    if (image) {
      setPreview(URL.createObjectURL(image));
    }
    setTimeout(() => {
      setShowSpin(false);
    }, 1200);
  }, [image]);

  return (
    <>
      {showspin ? (
        <Spiner />
      ) : (
        <div className="staff-add-form-bg">
          <div className="staff-add-container">
            <h2 className="text-center mt-1" id="title-text">
              Add Major Staff
            </h2>
            <Card className="shadow mt-3 p-3">
              <div className="profile_div text-center">
                <img
                  src={preview ? preview : "/add-major-staff.jpg"}
                  alt="hospital-staff"
                />
              </div>
              <Form autoComplete="off">
                <Row>
                  {/* Input fields for major staff */}
                  <Form.Group className="mb-3 col-lg-6">
                    <Form.Label>First name</Form.Label>
                    <Form.Control
                      type="text"
                      name="fname"
                      value={inputdata.fname}
                      onChange={setInputValue}
                      placeholder="Enter first name"
                    />
                  </Form.Group>
                  {/* Continue with similar input fields as in Doctor.js */}
                  {/* Submit Button */}
                  <Button
                    variant="primary"
                    type="submit"
                    onClick={submitUserData}
                  >
                    Add Major Staff
                  </Button>
                </Row>
              </Form>
            </Card>
            <ToastContainer position="top-center" />
          </div>
        </div>
      )}
    </>
  );
};

export default MajorStaff;
